      const { data } = response;
      setIsICMPInboundAllowed(data); // 요청 결과에 따라 상태를 업데이트
    } catch (error) {
      console.error('Error fetching data:', error);
      setIsICMPInboundAllowed(false); // 요청 실패 시 false로 설정
    }

  };

  // 페이지 변경 함수
