import React, { useState } from 'react';

function SignUpPage() {
  // 각 입력 필드의 상태를 관리하는 useState 훅을 사용합니다.
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');

  // 회원가입 버튼을 클릭할 때 실행되는 함수입니다.
  const handleSignUp = () => {
    const userData = {
        userId: userId,
        password: password,
        name: name
    };
    fetch('http://your.spring.server/signup', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(userData)
    })
    .then(response => response.json())
    .then(data => {
        console.log('회원가입 결과:', data);
        // 여기에 추가적인 처리를 할 수 있습니다. 예를 들어 성공 메시지를 사용자에게 보여줄 수 있습니다.
    })
    .catch(error => {
        console.error('회원가입 오류:', error);
        // 오류 처리 로직을 추가할 수 있습니다.
    });



    // 여기에 실제 회원가입 로직을 구현할 수 있습니다.
    // 서버로 회원가입 정보를 전송하거나, 다른 작업을 수행할 수 있습니다.
  };

  return (
    <div>
      <h2>회원가입</h2>
      <div>
        <label>
          ID: 
          <input 
            type="text" 
            value={userId} 
            onChange={(e) => setUserId(e.target.value)} 
          />
        </label>
      </div>
      <div>
        <label>
          Password: 
          <input 
            type="password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
          />
        </label>
      </div>
      <div>
        <label>
          이름: 
          <input 
            type="text" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
          />
        </label>
      </div>
      <button onClick={handleSignUp}>회원가입</button>
    </div>
  );
}

export default SignUpPage;
